export default function validateForm(values,props){
    console.log("Validacion")
    console.log(props)
    const err={};
    props.info.forEach(element => {
        if(values[element.id].length<element.min){
            err[element.id] =`Length of ${element.id} is too short`
        }
    });
    if(values.username.length===0){
        err.username  = "Length of username is too short"
    }
    if(values.email.length===0){
        err.email  = "Length of email is too short"
    }
    //todos los campos del formulario
    return err;
}
