import { useState } from "react";
//props en esta clase
const useForm =()=>{
    const [values,setValues] = useState({
        username:"",
        email:"",
        password:"",
        confirmPassword:""
    });


    const handleChange =(evt)=>{
        setValues({
            ...values, 
            [evt.target.name] : evt.target.value
        });
    }
    return {handleChange}
}
export default useForm;
