import React from "react";
import useForm from "../customHooks/useForm";
import validateForm from "../utils/validateForm";

const SignForm = (props) => {
    let campos = new Map();
    props.info.forEach(element => {
        campos.set(element.id,"");
    });
    console.log(campos);
  const { handleChange, handleSubmit, errors } = useForm(campos,validateForm);
  console.log(errors);
  console.log(props);
  return (
    <div>
      <h1>Register form</h1>
      <form onSubmit={handleSubmit}>
        {props.info.map((element) => {
          return (
            <div>
              <label htmlFor={element.id}>{element.placeholder}:</label>
              <input
                type={element.type}
                name={element.name}
                placeholder={element.placeholder}
                onChange={handleChange}
                id={element.id}
              />
            </div>
          );
        })}
        <button>Send</button>
      </form>
    </div>
  );
};
export default SignForm;
